@use "../abstracts//variables" as *;



.heading--primary{

    &--main,
    &--sub{
        text-align: center;
        text-transform:uppercase;
      
        display: block;
       

    }
    &--main{

        font-size:2.8rem;
        letter-spacing:1rem;
        font-weight: 400;
        animation: moveInLeft 1s linear;
    }

    &--sub{
        font-size: 1.6rem;
        letter-spacing: 1.2rem;
        font-weight: 700;
        animation: moveInRight 1s linear;
    }
}

.heading--secondary{
    font-size: 2.4rem;
    text-transform: capitalize;
    font-weight: 700;
    display: inline-block;
    margin-inline: auto;
    background-image:linear-gradient(to right ,$clr-primary-tint,$clr-primary-shade);
    color:transparent;
    -webkit-background-clip: text;
    transition: all 0.6s;

   
&:hover{
    transform: scaleX(1.1) skewX(10deg);
}

}

.heading--tertiary{
    font-size: 1.8rem;
    text-transform: capitalize;
    letter-spacing: 0.1rem;
}

.paragraph{
    font-size: 1.4rem;

    &:not(:last-child){
        margin-bottom: 3rem;
    }
}

.txt{
    -moz-hyphens: auto;
    -ms-hyphens: auto;
    -webkit-hyphens: auto;
     hyphens:auto;
     font-size: 1.4rem;
}


@media (min-width:44em){
    .heading--primary{
        &--main{

            font-size:4rem;
           
        }
    
        &--sub{
            font-size: 2.2rem;
            
        }
    }

    
.heading--secondary{
    font-size: 3rem;
}
.heading--tertiary{
    font-size: 2rem;
    line-height: 1.3;
}
.txt{
    font-size: 1.6rem;
}
}



@media (min-width:87em){
    .heading--primary{
        &--main{

            font-size:6rem;
           
        }
    
        &--sub{
            font-size: 2.2rem;
            letter-spacing: 1.4rem;
            
        }
    }

    
.heading--secondary{
    font-size: 3.4rem;
}



}

